import React, { useState, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Anomaly } from '../types';
import { ChevronDown, ChevronRight, Thermometer, CloudRain, AlertTriangle, Calendar, TrendingUp } from 'lucide-react';

interface AnomalyTimelineProps {
  anomalies: Anomaly[];
}

const AnomalyTimeline: React.FC<AnomalyTimelineProps> = ({ anomalies }) => {
  const [expandedEvent, setExpandedEvent] = useState<string | null>(null);

  // Group and sort anomalies by year and severity
  const timelineData = useMemo(() => {
    const grouped = anomalies.reduce((acc, anomaly) => {
      // Extract year from description
      const yearMatch = anomaly.description.match(/(\d{4})/);
      const year = yearMatch ? parseInt(yearMatch[1]) : 2024;

      if (!acc[year]) {
        acc[year] = [];
      }
      acc[year].push(anomaly);
      return acc;
    }, {} as { [key: number]: Anomaly[] });

    // Convert to timeline format and sort
    return Object.entries(grouped)
      .map(([year, yearAnomalies]) => ({
        year: parseInt(year),
        anomalies: yearAnomalies.sort((a, b) => {
          const severityOrder = { extreme: 4, high: 3, medium: 2, low: 1 };
          return severityOrder[b.severity as keyof typeof severityOrder] - 
                 severityOrder[a.severity as keyof typeof severityOrder];
        }),
        extremeCount: yearAnomalies.filter(a => a.severity === 'extreme').length,
        highCount: yearAnomalies.filter(a => a.severity === 'high').length,
        totalCount: yearAnomalies.length
      }))
      .sort((a, b) => b.year - a.year)
      .slice(0, 20); // Show last 20 years
  }, [anomalies]);

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'extreme': return 'bg-red-500';
      case 'high': return 'bg-orange-500';
      case 'medium': return 'bg-yellow-500';
      default: return 'bg-green-500';
    }
  };

  const getSeverityIcon = (dataType: string) => {
    return dataType === 'temperature' ? Thermometer : CloudRain;
  };

  const getEventSize = (severity: string, count: number) => {
    const baseSize = severity === 'extreme' ? 'w-6 h-6' : 
                    severity === 'high' ? 'w-5 h-5' : 'w-4 h-4';
    return baseSize;
  };

  const generateAIExplanation = (anomaly: Anomaly) => {
    const contextPhrases = [
      "This represents a significant deviation from normal climate patterns.",
      "Such extreme values are statistically rare and indicate unusual weather conditions.",
      "This anomaly suggests potential climate variability or extreme weather events.",
      "The magnitude of this deviation warrants attention from meteorological perspectives."
    ];

    const impactPhrases = [
      "This could impact agricultural activities and water resources.",
      "Such conditions may affect local ecosystems and weather patterns.",
      "This anomaly might influence seasonal weather predictions.",
      "The deviation could have implications for regional climate trends."
    ];

    return `${contextPhrases[Math.floor(Math.random() * contextPhrases.length)]} ${impactPhrases[Math.floor(Math.random() * impactPhrases.length)]}`;
  };

  return (
    <div className="card">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold text-gray-800 flex items-center gap-2">
          ðŸ“Š Weather Anomaly Timeline
        </h3>
        <div className="flex items-center gap-4 text-sm">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-red-500 rounded-full"></div>
            <span>Extreme</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-orange-500 rounded-full"></div>
            <span>High</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
            <span>Medium</span>
          </div>
        </div>
      </div>

      {/* Timeline Container */}
      <div className="relative">
        {/* Timeline Line */}
        <div className="absolute left-8 top-0 bottom-0 w-0.5 bg-gradient-to-b from-blue-500 via-purple-500 to-indigo-500"></div>

        {/* Timeline Events */}
        <div className="space-y-6">
          {timelineData.map((yearData, index) => (
            <motion.div
              key={yearData.year}
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
              className="relative pl-16"
            >
              {/* Year Marker */}
              <div className="absolute left-0 flex items-center">
                <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold shadow-lg">
                  {yearData.year}
                </div>
              </div>

              {/* Event Card */}
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
                {/* Header */}
                <div className="bg-gradient-to-r from-gray-50 to-gray-100 p-4 border-b">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <Calendar className="w-5 h-5 text-gray-600" />
                      <h4 className="font-semibold text-gray-800">
                        {yearData.totalCount} Anomal{yearData.totalCount === 1 ? 'y' : 'ies'} Detected
                      </h4>
                    </div>
                    <div className="flex items-center gap-2">
                      {yearData.extremeCount > 0 && (
                        <span className="bg-red-100 text-red-800 px-2 py-1 rounded-full text-xs font-semibold">
                          {yearData.extremeCount} Extreme
                        </span>
                      )}
                      {yearData.highCount > 0 && (
                        <span className="bg-orange-100 text-orange-800 px-2 py-1 rounded-full text-xs font-semibold">
                          {yearData.highCount} High
                        </span>
                      )}
                    </div>
                  </div>
                </div>

                {/* Anomaly Events */}
                <div className="p-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
                    {yearData.anomalies.slice(0, 6).map((anomaly, anomalyIndex) => {
                      const Icon = getSeverityIcon(anomaly.data_type);
                      const eventId = `${yearData.year}-${anomalyIndex}`;
                      const isExpanded = expandedEvent === eventId;

                      return (
                        <motion.div
                          key={anomalyIndex}
                          initial={{ opacity: 0, scale: 0.8 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ delay: (index * 0.1) + (anomalyIndex * 0.05) }}
                          className={`p-3 rounded-lg border-2 cursor-pointer transition-all ${
                            anomaly.severity === 'extreme' ? 'border-red-200 bg-red-50 hover:bg-red-100' :
                            anomaly.severity === 'high' ? 'border-orange-200 bg-orange-50 hover:bg-orange-100' :
                            anomaly.severity === 'medium' ? 'border-yellow-200 bg-yellow-50 hover:bg-yellow-100' :
                            'border-green-200 bg-green-50 hover:bg-green-100'
                          }`}
                          onClick={() => setExpandedEvent(isExpanded ? null : eventId)}
                        >
                          <div className="flex items-start justify-between">
                            <div className="flex items-center gap-2">
                              <div className={`p-1 rounded-full ${getSeverityColor(anomaly.severity)}`}>
                                <Icon className="w-3 h-3 text-white" />
                              </div>
                              <div className="flex-1 min-w-0">
                                <div className="text-sm font-semibold text-gray-800 truncate">
                                  {anomaly.data_type === 'temperature' ? 'Temperature' : 'Precipitation'}
                                </div>
                                <div className="text-xs text-gray-600">
                                  Z-score: {anomaly.z_score.toFixed(2)}
                                </div>
                              </div>
                            </div>
                            <div className="flex items-center gap-1">
                              <AlertTriangle className={`w-4 h-4 ${
                                anomaly.severity === 'extreme' ? 'text-red-500' :
                                anomaly.severity === 'high' ? 'text-orange-500' :
                                'text-yellow-500'
                              }`} />
                              {isExpanded ? 
                                <ChevronDown className="w-4 h-4 text-gray-400" /> : 
                                <ChevronRight className="w-4 h-4 text-gray-400" />
                              }
                            </div>
                          </div>

                          {/* Expanded Details */}
                          <AnimatePresence>
                            {isExpanded && (
                              <motion.div
                                initial={{ opacity: 0, height: 0 }}
                                animate={{ opacity: 1, height: 'auto' }}
                                exit={{ opacity: 0, height: 0 }}
                                className="mt-3 pt-3 border-t border-gray-200"
                              >
                                <div className="space-y-2 text-xs">
                                  <div className="flex justify-between">
                                    <span className="text-gray-600">Value:</span>
                                    <span className="font-semibold">{anomaly.value.toFixed(2)}</span>
                                  </div>
                                  <div className="flex justify-between">
                                    <span className="text-gray-600">Expected:</span>
                                    <span>{anomaly.expected_value.toFixed(2)}</span>
                                  </div>
                                  <div className="flex justify-between">
                                    <span className="text-gray-600">Deviation:</span>
                                    <span className="font-semibold text-red-600">
                                      {anomaly.deviation.toFixed(2)}
                                    </span>
                                  </div>
                                  <div className="mt-2 p-2 bg-white rounded text-gray-700 text-xs leading-relaxed">
                                    <div className="flex items-start gap-1">
                                      <TrendingUp className="w-3 h-3 text-blue-500 mt-0.5 flex-shrink-0" />
                                      <span>{generateAIExplanation(anomaly)}</span>
                                    </div>
                                  </div>
                                </div>
                              </motion.div>
                            )}
                          </AnimatePresence>
                        </motion.div>
                      );
                    })}
                  </div>

                  {/* Show More Button */}
                  {yearData.anomalies.length > 6 && (
                    <div className="mt-4 text-center">
                      <button className="text-blue-600 hover:text-blue-800 text-sm font-semibold">
                        Show {yearData.anomalies.length - 6} more anomalies...
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Summary Stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: timelineData.length * 0.1 }}
          className="mt-8 bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-6"
        >
          <h4 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <TrendingUp className="w-5 h-5 text-blue-600" />
            Timeline Summary
          </h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            <div>
              <div className="text-2xl font-bold text-gray-900">
                {timelineData.length}
              </div>
              <div className="text-sm text-gray-600">Years Analyzed</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-red-600">
                {timelineData.reduce((sum, year) => sum + year.extremeCount, 0)}
              </div>
              <div className="text-sm text-gray-600">Extreme Events</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-orange-600">
                {timelineData.reduce((sum, year) => sum + year.highCount, 0)}
              </div>
              <div className="text-sm text-gray-600">High Severity</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-blue-600">
                {timelineData.reduce((sum, year) => sum + year.totalCount, 0)}
              </div>
              <div className="text-sm text-gray-600">Total Anomalies</div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default AnomalyTimeline;
