import React, { useState } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

interface TimeseriesData {
  dates: string[];
  anomalies: number[];
  z_scores: number[];
  significant: boolean[];
  values: number[];
  historical_avg: number[];
}

interface WeatherTrendsProps {
  timeseriesData: {
    precipitation?: TimeseriesData;
    max_temperature?: TimeseriesData;
    min_temperature?: TimeseriesData;
  };
}

const WeatherTrends: React.FC<WeatherTrendsProps> = ({ timeseriesData }) => {
  const [activeTab, setActiveTab] = useState<'trends' | 'anomalies'>('trends');
  const [activeMetric, setActiveMetric] = useState<'precipitation' | 'max_temperature' | 'min_temperature'>('precipitation');

  if (!timeseriesData || Object.keys(timeseriesData).length === 0) {
    return (
      <div className="card animate-slide-up">
        <h3 className="text-xl font-bold text-gray-800 mb-4">
          üìä Weather Analysis
        </h3>
        <div className="text-center py-8 text-gray-500">
          No weather data available. Click "Run Detection" to load data.
        </div>
      </div>
    );
  }

  const currentData = timeseriesData[activeMetric];

  if (!currentData) {
    return (
      <div className="card animate-slide-up">
        <h3 className="text-xl font-bold text-gray-800 mb-4">
          üìä Weather Analysis
        </h3>
        <div className="text-center py-8 text-gray-500">
          No data available for {activeMetric.replace('_', ' ')}
        </div>
      </div>
    );
  }

  // Function to calculate annual averages and 10-year moving averages
  const calculateTrendData = () => {
    // Group data by year and calculate annual averages
    const yearlyData: { [year: number]: { values: number[], count: number } } = {};

    currentData.dates.forEach((dateStr, index) => {
      const year = new Date(dateStr).getFullYear();
      if (!yearlyData[year]) {
        yearlyData[year] = { values: [], count: 0 };
      }
      yearlyData[year].values.push(currentData.values[index]);
      yearlyData[year].count++;
    });

    // Calculate annual averages
    const years = Object.keys(yearlyData).map(Number).sort();
    const annualValues = years.map(year => {
      const sum = yearlyData[year].values.reduce((a, b) => a + b, 0);
      return sum / yearlyData[year].values.length;
    });

    // Calculate 10-year moving averages
    const movingAverages = [];
    const window = 10;

    for (let i = 0; i < annualValues.length; i++) {
      if (i < window - 1) {
        movingAverages.push(null); // Not enough data for moving average
      } else {
        const windowSum = annualValues.slice(i - window + 1, i + 1).reduce((a, b) => a + b, 0);
        movingAverages.push(windowSum / window);
      }
    }

    return {
      years: years.map(year => year.toString()),
      annualValues,
      movingAverages: movingAverages.filter(val => val !== null),
      movingAverageYears: years.slice(window - 1).map(year => year.toString())
    };
  };

  // Prepare data based on active tab
  const getChartData = () => {
    if (activeTab === 'trends') {
      // Show annual trends with moving averages
      const trendData = calculateTrendData();

      if (activeMetric === 'precipitation') {
        return {
          labels: trendData.years,
          datasets: [
            {
              label: 'Annual Precipitation',
              data: trendData.annualValues,
              borderColor: 'rgba(59, 130, 246, 0.4)',
              backgroundColor: 'rgba(59, 130, 246, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 4,
              borderWidth: 1,
              fill: false,
            },
            {
              label: 'Precipitation (10Y Avg)',
              data: trendData.movingAverageYears.map((year, index) => ({
                x: year,
                y: trendData.movingAverages[index]
              })),
              borderColor: '#059669',
              backgroundColor: 'rgba(5, 150, 105, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 5,
              borderWidth: 3,
              fill: false,
            }
          ]
        };
      } else {
        // For temperature, we need to calculate trends for all three metrics
        const precipData = timeseriesData.precipitation ? calculateTrendData() : null;
        const maxTempData = timeseriesData.max_temperature ? calculateTrendData() : null;
        const minTempData = timeseriesData.min_temperature ? calculateTrendData() : null;

        // Use the current metric's years as base
        const trendData = calculateTrendData();

        const datasets = [];

        if (activeMetric === 'max_temperature') {
          datasets.push(
            {
              label: 'Annual Max Temperature',
              data: trendData.annualValues,
              borderColor: 'rgba(239, 68, 68, 0.4)',
              backgroundColor: 'rgba(239, 68, 68, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 4,
              borderWidth: 1,
              fill: false,
            },
            {
              label: 'Max Temp (10Y Avg)',
              data: trendData.movingAverageYears.map((year, index) => ({
                x: year,
                y: trendData.movingAverages[index]
              })),
              borderColor: '#dc2626',
              backgroundColor: 'rgba(220, 38, 38, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 5,
              borderWidth: 3,
              fill: false,
            }
          );
        } else if (activeMetric === 'min_temperature') {
          datasets.push(
            {
              label: 'Annual Min Temperature',
              data: trendData.annualValues,
              borderColor: 'rgba(16, 185, 129, 0.4)',
              backgroundColor: 'rgba(16, 185, 129, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 4,
              borderWidth: 1,
              fill: false,
            },
            {
              label: 'Min Temp (10Y Avg)',
              data: trendData.movingAverageYears.map((year, index) => ({
                x: year,
                y: trendData.movingAverages[index]
              })),
              borderColor: '#059669',
              backgroundColor: 'rgba(5, 150, 105, 0.1)',
              tension: 0.1,
              pointRadius: 0,
              pointHoverRadius: 5,
              borderWidth: 3,
              fill: false,
            }
          );
        }

        return {
          labels: trendData.years,
          datasets
        };
      }
    } else {
      // Show anomalies with significant ones highlighted (keep existing logic)
      return {
        labels: currentData.dates,
        datasets: [
          {
            label: `${activeMetric === 'precipitation' ? 'Precipitation' : activeMetric.replace('_', ' ')} Anomalies`,
            data: currentData.anomalies,
            borderColor: activeMetric === 'precipitation' ? '#3b82f6' : 
                        activeMetric === 'max_temperature' ? '#ef4444' : '#10b981',
            backgroundColor: activeMetric === 'precipitation' ? 'rgba(59, 130, 246, 0.1)' : 
                            activeMetric === 'max_temperature' ? 'rgba(239, 68, 68, 0.1)' : 'rgba(16, 185, 129, 0.1)',
            tension: 0.1,
            pointRadius: currentData.significant.map(sig => sig ? 5 : 1),
            pointBackgroundColor: currentData.significant.map((sig, i) => 
              sig ? '#dc2626' : (activeMetric === 'precipitation' ? '#3b82f6' : 
                               activeMetric === 'max_temperature' ? '#ef4444' : '#10b981')
            ),
            pointBorderColor: currentData.significant.map(sig => sig ? '#dc2626' : '#ffffff'),
            pointBorderWidth: 1,
            pointHoverRadius: 6,
            borderWidth: 1.5,
            fill: false,
          }
        ]
      };
    }
  };

  const chartData = getChartData();

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index' as const,
      intersect: false,
    },
    plugins: {
      legend: {
        position: 'top' as const,
        labels: {
          color: '#374151',
          font: {
            size: 12
          }
        }
      },
      title: {
        display: true,
        text: activeTab === 'trends' ? 
          `${activeMetric === 'precipitation' ? 'Annual Precipitation in Pakistan (1901‚Äì2024) with 10-Year Moving Average' : 
            `Average Annual ${activeMetric.replace('_', ' ')} in Pakistan (1901‚Äì2024) with 10-Year Moving Average`}` :
          `${activeMetric === 'precipitation' ? 'Precipitation' : activeMetric.replace('_', ' ')} Anomalies (1901-2024)`,
        color: '#1f2937',
        font: {
          size: 16,
          weight: 'bold' as const
        }
      },
      tooltip: {
        callbacks: {
          title: (context: any) => {
            if (activeTab === 'trends') {
              return `Year: ${context[0].label}`;
            } else {
              const date = new Date(context[0].label);
              return date.toLocaleDateString('en-US', { year: 'numeric', month: 'short' });
            }
          },
          label: (context: any) => {
            const unit = activeMetric === 'precipitation' ? 'mm' : '¬∞C';

            if (activeTab === 'trends') {
              return `${context.dataset.label}: ${context.parsed.y.toFixed(2)}${unit}`;
            } else {
              const index = context.dataIndex;
              const zScore = currentData.z_scores[index];
              const isSignificant = currentData.significant[index];
              return [
                `Anomaly: ${context.parsed.y.toFixed(2)}${unit}`,
                `Z-Score: ${zScore.toFixed(2)}`,
                `Significant: ${isSignificant ? 'Yes (|Z| ‚â• 2.0)' : 'No'}`
              ];
            }
          }
        }
      }
    },
    scales: {
      x: {
        type: activeTab === 'trends' ? 'category' as const : 'time' as const,
        ...(activeTab === 'anomalies' && {
          time: {
            unit: 'year' as const,
            displayFormats: {
              year: 'yyyy'
            }
          }
        }),
        title: {
          display: true,
          text: 'Year',
          color: '#374151',
          font: {
            size: 14
          }
        },
        grid: {
          color: '#e5e7eb',
          lineWidth: 0.5
        },
        ticks: {
          color: '#6b7280',
          ...(activeTab === 'trends' && {
            callback: function(value: any, index: number) {
              // Show every 10th year for trends to avoid crowding
              const year = parseInt(this.getLabelForValue(value));
              return year % 10 === 0 ? year : '';
            }
          })
        }
      },
      y: {
        title: {
          display: true,
          text: activeTab === 'trends' ? 
            `${activeMetric === 'precipitation' ? 'Precipitation (mm)' : 'Temperature (¬∞C)'}` :
            `Anomaly (${activeMetric === 'precipitation' ? 'mm' : '¬∞C'})`,
          color: '#374151',
          font: {
            size: 14
          }
        },
        grid: {
          color: '#e5e7eb',
          lineWidth: 0.5
        },
        ticks: {
          color: '#6b7280'
        }
      }
    }
  };

  // Calculate summary statistics
  const significantCount = currentData.significant.filter(Boolean).length;
  const totalCount = currentData.dates.length;

  return (
    <div className="card animate-slide-up">
      {/* Header with tabs */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6 gap-4">
        <h3 className="text-xl font-bold text-gray-800">
          üìä Weather Analysis Dashboard
        </h3>

        {/* View Toggle */}
        <div className="flex bg-gray-100 rounded-lg p-1">
          <button
            onClick={() => setActiveTab('trends')}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'trends'
                ? 'bg-blue-500 text-white shadow-sm'
                : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            üìà Trends
          </button>
          <button
            onClick={() => setActiveTab('anomalies')}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeTab === 'anomalies'
                ? 'bg-red-500 text-white shadow-sm'
                : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            üö® Anomalies
          </button>
        </div>
      </div>

      {/* Metric selector */}
      <div className="flex justify-center mb-6">
        <div className="flex bg-gray-50 rounded-lg p-1 gap-1">
          <button
            onClick={() => setActiveMetric('precipitation')}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeMetric === 'precipitation'
                ? 'bg-blue-500 text-white shadow-sm'
                : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            üåßÔ∏è Precipitation
          </button>
          <button
            onClick={() => setActiveMetric('max_temperature')}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeMetric === 'max_temperature'
                ? 'bg-red-500 text-white shadow-sm'
                : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            üî• Max Temperature
          </button>
          <button
            onClick={() => setActiveMetric('min_temperature')}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              activeMetric === 'min_temperature'
                ? 'bg-green-500 text-white shadow-sm'
                : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            ‚ùÑÔ∏è Min Temperature
          </button>
        </div>
      </div>

      {/* Chart */}
      <div className="h-96 mb-6">
        <Line data={chartData} options={options} />
      </div>

      {/* Summary Statistics */}
      {activeTab === 'anomalies' && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 bg-gray-50 p-4 rounded-lg">
          <div className="text-center">
            <p className="font-semibold text-gray-700">Total Records</p>
            <p className="text-2xl font-bold text-blue-600">{totalCount}</p>
            <p className="text-xs text-gray-500">1901-2024</p>
          </div>
          <div className="text-center">
            <p className="font-semibold text-gray-700">Significant Anomalies</p>
            <p className="text-2xl font-bold text-red-600">{significantCount}</p>
            <p className="text-xs text-gray-500">{((significantCount / totalCount) * 100).toFixed(1)}% of records</p>
          </div>
          <div className="text-center">
            <p className="font-semibold text-gray-700">Detection Threshold</p>
            <p className="text-2xl font-bold text-gray-600">|Z| ‚â• 2.0</p>
            <p className="text-xs text-gray-500">Statistical significance</p>
          </div>
        </div>
      )}

      {/* Trend Statistics */}
      {activeTab === 'trends' && (
        <div className="bg-gray-50 p-4 rounded-lg">
          <div className="text-center">
            <p className="font-semibold text-gray-700">Long-term Climate Trends (1901-2024)</p>
            <p className="text-sm text-gray-600 mt-2">
              üìà <strong>Annual Data:</strong> Light line shows yearly averages<br/>
              üìä <strong>10-Year Moving Average:</strong> Bold line shows smoothed long-term trends
            </p>
          </div>
        </div>
      )}

      {/* Explanation */}
      <div className="mt-4 text-sm text-gray-600">
        <p>
          {activeTab === 'trends' ? (
            <><strong>Trends View:</strong> Shows annual averages (light line) and 10-year moving averages (bold line) to reveal long-term climate patterns and reduce year-to-year noise.</>
          ) : (
            <><strong>Anomalies View:</strong> Shows deviations from historical norms. Red dots indicate statistically significant anomalies (|Z-score| ‚â• 2.0) using monthly baseline calculations.</>
          )}
        </p>
      </div>
    </div>
  );
};

export default WeatherTrends;
