import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ScatterController
} from 'chart.js';
import { Scatter } from 'react-chartjs-2';
import { Anomaly } from '../types';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ScatterController
);

interface AnomalyChartProps {
  anomalies: Anomaly[];
}

const AnomalyChart: React.FC<AnomalyChartProps> = ({ anomalies }) => {
  // Extract year from description and prepare data for scatter plot
  const chartData = anomalies.map(anomaly => {
    // Try to extract year from description
    const yearMatch = anomaly.description.match(/(\d{4})/);
    const year = yearMatch ? parseInt(yearMatch[1]) : 2020;

    return {
      x: year,
      y: Math.abs(anomaly.z_score),
      severity: anomaly.severity,
      type: anomaly.anomaly_type,
      description: anomaly.description,
      value: anomaly.value
    };
  });

  // Separate data by severity for different colors
  const extremeData = chartData.filter(d => d.severity === 'extreme');
  const highData = chartData.filter(d => d.severity === 'high');
  const mediumData = chartData.filter(d => d.severity === 'medium');
  const lowData = chartData.filter(d => d.severity === 'low');

  const data = {
    datasets: [
      {
        label: 'Extreme Anomalies',
        data: extremeData,
        backgroundColor: 'rgba(239, 68, 68, 0.8)',
        borderColor: 'rgba(239, 68, 68, 1)',
        pointRadius: 8,
        pointHoverRadius: 10,
      },
      {
        label: 'High Severity',
        data: highData,
        backgroundColor: 'rgba(245, 101, 101, 0.8)',
        borderColor: 'rgba(245, 101, 101, 1)',
        pointRadius: 6,
        pointHoverRadius: 8,
      },
      {
        label: 'Medium Severity',
        data: mediumData,
        backgroundColor: 'rgba(251, 191, 36, 0.8)',
        borderColor: 'rgba(251, 191, 36, 1)',
        pointRadius: 5,
        pointHoverRadius: 7,
      },
      {
        label: 'Low Severity',
        data: lowData,
        backgroundColor: 'rgba(59, 130, 246, 0.8)',
        borderColor: 'rgba(59, 130, 246, 1)',
        pointRadius: 4,
        pointHoverRadius: 6,
      }
    ]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top' as const,
        labels: {
          color: '#374151',
          font: {
            size: 12
          }
        }
      },
      title: {
        display: true,
        text: 'Weather Anomalies by Year and Severity (Z-Score)',
        color: '#1f2937',
        font: {
          size: 16,
          weight: 'bold' as const
        }
      },
      tooltip: {
        callbacks: {
          title: (context: any) => {
            const point = context[0];
            return `Year: ${point.parsed.x}`;
          },
          label: (context: any) => {
            const point = context.raw;
            return [
              `Z-Score: ${point.y.toFixed(2)}`,
              `Type: ${point.type}`,
              `Severity: ${point.severity}`,
              `Value: ${point.value.toFixed(2)}`,
              `Description: ${point.description.substring(0, 50)}...`
            ];
          }
        }
      }
    },
    scales: {
      x: {
        type: 'linear' as const,
        position: 'bottom' as const,
        title: {
          display: true,
          text: 'Year',
          color: '#374151',
          font: {
            size: 14
          }
        },
        grid: {
          color: '#e5e7eb'
        },
        ticks: {
          color: '#6b7280'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Z-Score (Absolute Value)',
          color: '#374151',
          font: {
            size: 14
          }
        },
        grid: {
          color: '#e5e7eb'
        },
        ticks: {
          color: '#6b7280'
        }
      }
    }
  };

  return (
    <div className="card animate-slide-up">
      <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
        ðŸ“ˆ Anomaly Distribution Over Time
      </h3>
      <div className="h-96">
        <Scatter data={data} options={options} />
      </div>
      <div className="mt-4 text-sm text-gray-600">
        <p>
          <strong>Higher Z-scores indicate more extreme anomalies.</strong> 
          Z-score â‰¥2.5 = Extreme, â‰¥2.0 = High, â‰¥1.5 = Medium
        </p>
      </div>
    </div>
  );
};

export default AnomalyChart;
